import React, { useEffect, useState } from "react";
import axios from "axios";
import { useSelector, useDispatch } from "react-redux";
import { setViewFalse } from "../store/index";

const CourseInfo = () => {
  const dispatch = useDispatch();
  const [courseDetails, updateCourseDetail] = useState("");
  const courseid = useSelector((state) => state.course); //
  const user = useSelector((state) => state.details); //

  useEffect(() => {
    axios.get(`http://localhost:5000/course/${courseid}`).then((response) => {
      updateCourseDetail(response.data);
    });
  });

  return (
    <>
      <header className="pt-4 pl-4 align-middle justify-between flex">
        <span className="font-semibold text-xl">User: {user.name}</span>
        <button
          className="w-auto mr-4 bg-blue-700 text-white font-semibold p-2 px-3 rounded-full border-none"
          onClick={() => dispatch(setViewFalse())}
        >
          Go Back
        </button>
      </header>
      <article className="flex flex-wrap">
        {courseData.map((course, index) => (
        <div key={course.id} className="w-full md:w-1/2 p-4">
          <div className="rounded-lg shadow-lg">
            <div className="px-6 py-4">
              <div className="mb-3">
                <h5 className="text-xl font-semibold mb-2  text-black">
                  {course.name}
                </h5>
                <p className="text-sm">{course.duration}</p>
              </div>
              <p className="text-sm font-semibold text-black">
                {course.instructor}
              </p>
              <p className="text-sm">{course.location}</p>
              <div className="flex w-full justify-between items-center">
                <a className="text-gray-600 rounded font-bold">
                  Enrollment Status : {course.enrollment_status}
                </a>
                <a
                  className="bg-blue-500 px-3 py-2 rounded-full text-white cursor-pointer"
                  onClick={() => handleCourseClick(course.id)}
                >
                  View Details
                </a>
              </div>
            </div>
          </div>
        </div>
        ))}
      </article>
    </>
  );
};

export default CourseInfo;
