import React, { useState } from "react";
import axios from "axios";
import { useSelector, useDispatch } from "react-redux";
// import { login, logout, setUserDetails } from "./store/index";

const Login = () => {
  const [studID, setStudID] = useState(null);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  // const loginStatus = useSelector((state) => state.logged);
  // const dispatch = useDispatch();

  // just for demo purposes
  const studentEmails = {
    "student1@example.com": 1,
    "student2@example.com": 2,
    "student3@example.com": 3,
    "student4@example.com": 4,
  };

  const fetchUserDetails = async () => {
    if (studentEmails[username] && password == "admin") {
      setStudID = studentEmails[username];
      axios
        .get(`http://localhost:5000/user/${studID}/details`)
        .then((response) => {
          alert("Logged In Successfully");
        })
        .catch((error) =>
          console.error("Error fetching course details:", error)
        );
    } else {
      alert(`Wrong credentials
      TIP: Available usernames are,
        student1@example.com
        student2@example.com
        student3@example.com
        student4@example.com
        Password: admin
      `);
    }
  };

  return (
    <article>
      <div className="flex mx-auto">
        <div className="login-container">
          <div className="text-3xl mb-3 font-semibold">Login</div>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <button onClick={fetchUserDetails}>Login</button>
        </div>
      </div>
    </article>
  );
};

export default Login;
