import React, { useState } from "react";
import axios from "axios";
import { useSelector, useDispatch } from "react-redux";
import {logout,setCurrCourseView,setViewTrue} from '../store/index'

const CoursesDisplay = () => {

  const [courseData, setCourseData] = useState(null)
  const userDetails = useSelector((state) => state.details);
  const dispatch = useDispatch()
  const viewCourse = useSelector((state) => state.viewing)
  
  const handleCourseClick = (id) => {
    dispatch(setCurrCourseView(id))
    dispatch()
  }

  axios
    .get("http://localhost:5000/courses")
    .then((response) => {
      setCourseData(response.data);
    })
    .catch((error) => console.error("Error fetching course details:", error));

  return (
    <>
      <div className="pt-4 pl-4 justify-between flex">
        <span className="font-semibold text-xl line-">User: {userDetails.name}</span>
        <button className="w-auto mr-4 bg-red-700 text-white font-semibold py-2 px-3 rounded-full"
        onClick={()=>dispatch(logout())}>
          Logout
        </button>
      </div>
      <article className="flex flex-wrap">
        {courseData &&
          courseData.map((course, index) => (
            <div key={course.id} className="w-full md:w-1/2 p-4">
              <div className="rounded-lg shadow-lg">
                <div className="px-6 py-4">
                  <div className="mb-3">
                    <h5 className="text-xl font-semibold mb-2  text-black">
                      {course.name}
                    </h5>
                    <p className="text-sm">{course.duration}</p>
                  </div>
                  <p className="text-sm font-semibold text-black">
                    {course.instructor}
                  </p>
                  <p className="text-sm">{course.location}</p>
                  <div className="flex w-full justify-between items-center">
                    <a className="text-gray-600 rounded font-bold">
                      Enrollment Status : {course.enrollment_status}
                    </a>
                    <a
                      className="bg-blue-500 px-3 py-2 rounded-full text-white cursor-pointer"
                      href={`http://localhost:3000/course/${course.id}`}
                    >
                      View Details
                    </a>
                  </div>
                </div>
              </div>
            </div>
          ))}
      </article>
    </>
  );
};

export default CoursesDisplay;
