import React from "react";
import { useState, useEffect } from "react";
import axios from "axios";
import { useSelector, useDispatch } from "react-redux";
import { login, logout, setUserDetails } from "../store/index";

const CourseDetails = () => {
  const courseid = useSelector((state) => state.course);
  const user = useSelector((state) => state.details);
  const [course, updateCourseDetail] = useState(null);

  axios
    .get(`http://localhost:5000/courses/${courseid}`)
    .then((response) => updateCourseDetail(response.data))
    .catch((error) => console.error("Error fetching course details:", error));
  

  const [showSyllabus, setShowSyllabus] = useState(false);

  const toggleSyllabus = () => {
    setShowSyllabus(!showSyllabus);
  };

  return (
    <>
      <div className="pt-4 pl-4 align-middle justify-between flex">
        <span className="font-semibold text-xl">User: {user.name}</span>
        <button className="w-auto mr-4 bg-red-700 text-white font-semibold p-2 px-3 rounded-full border-none">
          Logout
        </button>
      </div>
      <article className="flex flex-wrap" data-theme="light">
        <div className="flex max-w-md mx-auto bg-white shadow-md rounded-lg overflow-hidden w-full md:w-1/2 p-4 mt-10">
          <div className="p-4">
            <div className="flex justify-center">
              <img
                className="w-3/5"
                src={course.image}
                alt="course image"
                srcset=""
              />
            </div>
            <h2 className="text-2xl font-bold mb-2">{course.course_name}</h2>
            <p className="text-gray-600 mb-4">{course.description}</p>
            <div className="flex flex-wrap mb-4">
              <div className="w-full sm:w-1/2">
                <p>
                  <span className="font-semibold">Instructor:</span>{" "}
                  {course.instructor_name}
                </p>
                <p>
                  <span className="font-semibold">Enrollment Status:</span>{" "}
                  {course.enrollment_status}
                </p>
                <p>
                  <span className="font-semibold">Course Duration:</span>{" "}
                  {course.course_duration}
                </p>
                <p>
                  <span className="font-semibold">Pre-requisites:</span>{" "}
                  {course.prerequisites}
                </p>
              </div>
              <div className="w-full sm:w-1/2">
                <p>
                  <span className="font-semibold">Schedule:</span>{" "}
                  {course.schedule}
                </p>
                <p>
                  <span className="font-semibold">Location:</span>{" "}
                  {course.location}
                </p>
              </div>
            </div>
            <div>
              <div onClick={toggleSyllabus} className="cursor-pointer">
                <span className="font-semibold">Syllabus</span>
                <span className="pl-2 font-light from-neutral-500 italic">
                  (Click to expand)
                </span>
              </div>
              {showSyllabus && (
                <ul className="list-disc pl-4">
                  {course.syllabus.split("\n").map((item, index) => (
                    <li key={index}>{item}</li>
                  ))}
                </ul>
              )}
            </div>
          </div>
        </div>
      </article>
    </>
  );
};

export default CourseDetails;
